package com.tastyeem.apichatter.models.viewimport com.tastyeem.apichatter.models.db.Userimport java.util.*data class UserView(    var id: String = UUID.randomUUID().toString(),    var nickname: String? = null,    var avatarUrl: String? = null) {    constructor(userDb: User): this() {        this.id = userDb.id        this.nickname = userDb.nickname        this.avatarUrl = userDb.avatarUrl    }}