package com.tastyeem.apichatter.utilsimport com.tastyeem.apichatter.AppConstimport io.jsonwebtoken.Jwtsimport io.jsonwebtoken.SignatureAlgorithmimport java.util.*class TokenUtils {        fun getJWTToken(username: String): String? {            val tokenData: HashMap<String, Any> = HashMap()            tokenData.put("username", username)            tokenData.put("token_create_date", Date().time)            val calendar = Calendar.getInstance()            calendar.add(Calendar.YEAR, 100)            tokenData.put("token_expiration_date", calendar.time)            val jwtBuilder = Jwts.builder()            jwtBuilder.setExpiration(calendar.time)            jwtBuilder.setClaims(tokenData)            return jwtBuilder.signWith(SignatureAlgorithm.HS512, AppConst.JWT_SECRET).compact()        }}