package com.tastyeem.apichatter.utilsimport com.tastyeem.apichatter.AppConstimport io.jsonwebtoken.Jwtsimport io.jsonwebtoken.SignatureAlgorithmimport java.util.*class TokenUtils {        fun getJWTToken(userId: String): String? {            val tokenData: HashMap<String, Any> = HashMap()            tokenData["userId"] = userId            tokenData["token_create_date"] = Date().time            val calendar = Calendar.getInstance()            calendar.add(Calendar.MONTH, 1)            tokenData["token_expiration_date"] = calendar.time            val jwtBuilder = Jwts.builder()            jwtBuilder.setExpiration(calendar.time)            jwtBuilder.setClaims(tokenData)            return jwtBuilder.signWith(SignatureAlgorithm.HS512, AppConst.JWT_SECRET).compact()        }}